import React from 'react';
import { connect } from 'react-redux';
import { Button, Row, Form, Container, Col } from "shards-react";
import Web3 from "web3";
import Moment from "react-moment";

const UpdateForm =  ({ drizzle, drizzleState, assetPrice, listPrice, propAddress, orderId, prevOrderStatus, expiresAt, currentExpiresAt, storedOrderId  }) => { 


 const update = async () => {
  const account = await drizzleState.accounts[0];
  const contract = await drizzle.contracts[`${propAddress}`];
  //uint256 _newPrice, uint256 _expiresAt, bytes16 _orderId
  try {
      contract.methods["updateContract"].cacheSend(assetPrice, currentExpiresAt, expiresAt, {from:`${account}`});
    } catch (err) {
      console.error("Err:", err)
    }
  }

  
if(!prevOrderStatus && !listPrice && !expiresAt) {  
  return <></>;
}

// if(prevOrderStatus && listPrice && expiresAt)  {
//   return <>k</>;
// }
else if(prevOrderStatus === 'cancelled') {
  //else if(!prevOrderStatus) {
    return (
      <div className="funds-info-container update-text">
        <Container className="funds-info-container mt-2">
          <Row className="mt-2 mb-1 justify-content-center">
            <div className="">
              <h5 className="text-dark text-center display-5 mb-1">Update Required</h5>
                {prevOrderStatus === 'cancelled' ?
                  <> 
                    <Row className="mt-1 mb-1">
                      <Col className="col-4 px-1">
                        <div className="order-id-text d-inline-block">New Id</div>
                      </Col>
                      <Col className="col-6 px-1">
                        <div className="text-primary order-id-text d-inline-block text-truncate">{orderId}</div>
                      </Col>
                    </Row>
                    <Row className="mt-1 mb-1">
                      <Col className="col-4 px-1">
                        <div className="order-id-text d-inline-block">Old Id</div>
                      </Col>
                      <Col className="col-6 px-1">
                        <div className="text-danger order-id-text d-inline-block text-truncate">{storedOrderId}</div>
                      </Col>
                    </Row>
                  </>
                :
                  <></>
                }

                {listPrice != assetPrice && listPrice != 0 ?
                  <>
                    <Row className="mt-1 mb-1">
                      <Col className="col-6 px-1">
                        <div className="text-dark text-primary amount-text">New Price</div>
                      </Col>
                      <Col className="col-6 px-1">
                        <div className="amount-number">{Web3.utils.fromWei(`${assetPrice}`,'ether')}<span>&nbsp;MANA</span></div>
                      </Col>
                    </Row>
                    <Row className="mt-1 mb-1">
                      <Col className="col-6 px-1">
                        <div className="order-id-text d-inline-block">Old Price</div>
                      </Col>
                      <Col className="col-6 px-1">
                        <div className="original-amount-number">{Web3.utils.fromWei(`${listPrice}`,'ether')}<span>&nbsp;MANA</span></div>
                      </Col>
                    </Row>
                  </>
                :
                  <>
                  </>
              }
 
              {expiresAt != 0 && expiresAt != currentExpiresAt ?
                <>   
                  <Row className="mt-1">
                    <Col className="col-6 px-1">
                      <div className="text-dark text-danger mr-1 card-text">Old Date</div>
                    </Col>
                    <Col className="col-6 px-1">
                      <div className=" expire-text card-text">
                        {(typeof(Number(expiresAt)) == 'number')?
                          <Moment format="h:mma M/DD/YY">
                            {Number(expiresAt)}
                          </Moment>  
                    
                        :
                          'no end date'
                        }
                      </div> 
                    </Col>   
                  </Row>  
                  <Row className="mt-1">
                    <Col className="col-5 px-1">
                      <div className="text-dark text-primary mr-1 card-text">New Date</div>
                    </Col>
                    <Col className="col-6 px-1">
                      <div className="expire-text card-text">
                        {(typeof(Number(currentExpiresAt)) == 'number')?
                          <Moment format="h:mma M/DD/YY">
                            {Number(currentExpiresAt)}
                          </Moment>       
                        :
                          'no end date'
                        }
                      </div> 
                    </Col>  
                  </Row>  
                </>
              :
                <>
                </>        
              }
                  <Row className="justify-content-center">
                    <Form onSubmit={update}>
                      <Button outline theme="success" size="sm" className="mt-2 mx-auto"
                        key="submit"
                        type="button"
                        onClick={update} 
                        style={{ fontSize: 12 }}
                      >
                        Update Contract
                      </Button>
                    </Form>
                  </Row>
  
            </div> 
          </Row>
          <style jsx> {` 
              .{update-text:font-size:.9rem;font-family: Poppins;}
              .order-id-text{max-width: 150px;color:#000;}
              .amount-text {color:#000;}
              .amount-number {color:#66a3ff;}
              .original-amount-number{color:#c4183c;}
              .expire-text {color:#000;}
            `}    
          </style>
        {/* </Accordion> */}
        </Container>
      </div>
    )
  }
  else{
    return <>ddddd</>;
  }


}
  

const mapStateToProps = (state) => ({
  listPrice:state.appReducers.listPrice,
  assetPrice:state.appReducers.assetPrice,
  expiresAt:state.appReducers.expiresAt,
  currentExpiresAt:state.appReducers.currentExpiresAt,
  storedOrderId:state.appReducers.storedOrderId
})



export default connect(mapStateToProps, null)(UpdateForm);
  
  
  //export default UpdateForm;
  
  
  
  // <div className="funds-info-container">
  // <Container className="funds-info-container mt-2">
  //   <Row>
  //     <Col sm="12" md="4">
  //       <p className="amount-text mt-1 mb-1 text-left" >Pool Depth</p>
  //     </Col>
  //     <Col sm="12" md="4">
  //       <p className="amount-text mt-1 mb-1 text-center">{minRange}</p>
  //     </Col>
  //     <Col sm="12" md="4">
  //       <p className="amount-text mt-1 mb-1 text-center">MANA</p>
  //     </Col>
  //   </Row>  
  //   <Row>
  //     <Col sm="12" md="4">
  //       <p className="amount-text mt-1 mb-1 text-left" >Funds Needed</p>
  //     </Col>
  //     <Col sm="12" md="4">
  //     <p className="amount-text mt-1 mb-1 text-center">{fundsNeeded}</p>
  //     </Col>
  //     <Col sm="12" md="4">
  //       <p className="amount-text mt-1 mb-1 text-center">MANA</p>
  //     </Col>
  //   </Row>
  //   <Row>
  //     <ContractData drizzle={drizzle} drizzleState={drizzleState} contract={propAddress} method="proposals" methodArgs={[tokenId]} 
  //       render={data => (
  //         <>
  //           <Col sm="12" md="4">
  //             <p className="amount-text mt-1 mb-1 text-left" >Resale Price</p>
  //           </Col>
  //           <Col sm="12" md="4">
  //           <p className="amount-text mt-1 mb-1 text-center">{Web3.utils.fromWei(`${data.reSalePrice}`,'ether')}</p>
  //           </Col>
  //           <Col sm="12" md="4">
  //             <p className="amount-text mt-1 mb-1 text-center">MANA</p>
  //           </Col>
  //         </>
  //       )} >
  //     </ContractData>
  //   </Row>  
  // </Container>
  // </div>
