import React from 'react';
import { newContextComponents } from "@drizzle/react-components";
import { Button, Form } from "shards-react";
import Web3 from "web3";

const { ContractForm, ContractData } = newContextComponents;

const ClaimBtn = ({drizzle, drizzleState, propAddress, winner }) => {
  const account = drizzleState.accounts[0];

  return(
    <>
    {account == winner ?
      <div className="p-3 mt-3 border rounded">
        <div>
          <ContractData drizzle={drizzle} drizzleState={drizzleState} contract={propAddress} method="winningContributor" 
            render={data => (
              <>
                <p className="sell-text card-title text-center">Winner:&nbsp;{data}</p>
              </>
            )}
          />
        </div> 
        <ContractForm
          drizzle={drizzle}
          drizzleState={drizzleState} 
          contract={propAddress}
          method="claimAsset" 
          sendArgs={{ from:account}}
          render={({ handleSubmit }) => (
          <Form onSubmit={handleSubmit}>
            <div>
              <button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 16 }} > 
                Claim
              </button>
            </div>
            <style jsx> {` 
              .sell-text{font-weight:400;color:black;font-size:1rem;font-family: Poppins;}
              .amount-number {color:#66a3ff;font-family: Poppins} 
              `}    
            </style>
          </Form>
          )}
        />
      </div>
    :
      <div>
        <ContractData drizzle={drizzle} drizzleState={drizzleState} contract={propAddress} method="winningContributor" 
          render={data => (
            <>
              <p className="sell-text card-title text-center">Winner:&nbsp;{data}</p>
            </>
          )}
        />
        <style jsx> {` 
          .sell-text{font-weight:400;color:black;font-size:.8rem;font-family: Poppins;}
          .amount-number {color:#66a3ff;font-family: Poppins} 
          `}    
        </style>
      </div> 
 
    }       
    </>
  )
}

export default ClaimBtn;
