// render(<App />);


const NominationModal = ({ drizzleState, drizzle, parcel }) => {

  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (   
        <>
         <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Nominate Asset</button>
          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title className="text-center">Asset Price:&nbsp;{parcel.publication.price}&nbsp;MANA</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <div>
                <div>
                  <div>
                  <NominationForm                   
                    drizzle={drizzle}
                    drizzleState ={drizzleState}  
                    assetPrice={parcel.publication.price}  
                    /> 
                  </div>  
                </div>
              </div>
            </Modal.Body>
          </Modal>
        </>
         );    
        } 

    export default NominationModal
  ;
