import React from "react";
import { Button } from "shards-react";
import Web3 from "web3";
import { toast } from 'react-toastify';
import Modal from 'react-bootstrap/Modal';
import ContributionButton from '../buttons/ContributionButton';
// import Contribute from './Contribute.js';
import { newContextComponents } from "@drizzle/react-components";

const { AccountData, ContractData, ContractForm } = newContextComponents;


const Contribute = ({ drizzle, drizzleState, accounts }) => {
  {console.log(accounts[0])}
  return (
   
    <div>
      <div className="section">
      <h2>Active Account</h2>
      <AccountData  drizzle={drizzle} drizzleState={drizzleState} accountIndex={0} units="ether" precision={3} />
      </div>
      <div className="section">
      <p>
        <strong>My Balance: </strong>
        <ContractData  
          drizzle={drizzle} 
          drizzleState={drizzleState} 
          contract="MANAToken"  
          method="balanceOf"
          methodArgs={[accounts[0]]} />
      </p>
      <ContractForm drizzle={drizzle} drizzleState={drizzleState} contract="MANAToken" method="approve"
      labels={["Spender", "Amount"]}
      />
      </div>
    </div>
  )
}

export default Contribute;
