import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Web3 from "web3";
import { Loader } from 'rimble-ui';
import { Card, CardHeader, CardBody, CardFooter, CardTitle, Button, Row } from "shards-react";
import NominationModal from './modals/NominationModal';
import FundingModal from './modals/FundingModal.js';
import SaleModal from './modals/SaleModal.js';
import SoldModal from './modals/SoldModal.js';
import PaidOutModal from './modals/PaidOutModal.js';
import PurchaseModal from './modals/PurchaseModal.js';
import NominationDetailsLink from './NominationDetailsLink.js';
import CardFooterButton from './buttons/CardFooterBtn.js';
import Moment from "react-moment";




const Nominations =  ({ orders, loading, props, userManaBalance, getSoldStatus, getOrderId, storedOrderId }) => {
  
  const [isStatus, setStatus] = useState(null);
// const [isStoredOrderId, setStoredOrderId] = useState();
console.log(storedOrderId)
const fetchProposals = async () => {
  const proposal = await props.drizzle.contracts.ProposalController.methods.deployedProposals(`${tokenId}`).call();
  if(proposal){
    const proposalAddress = await props.drizzle.contracts.ProposalController.methods.proposalTokenIdMap(`${tokenId}`).call();
    const proposalBalance = await props.drizzle.contracts.MANAToken.methods.balanceOf(`${proposalAddress}`).call();
    const propData = await props.drizzle.contracts.ProposalController.methods.getPropData(`${proposalAddress}`).call();
    const status = propData[0];
    getOrderId(propData[2]);
    if(status === '4'){
      if(proposalBalance > assetPrice){
        const soldStatus = '6';
        getSoldStatus(proposalBalance, soldStatus);
        setStatus('6');
      }
        else{
          setStatus(status);
          getOrderId(orderId);
        }
    }
      else {
        setStatus(status);
        getOrderId(orderId);
      }
    }
    else{
      setStatus(null)
      getOrderId(orderId);
    }
  }

  useEffect(() => {
    fetchProposals();
  }, [tokenId]);




  if(loading) {
    return <Loader className="mt-2 mx-auto" size="30px" color="blue" />
  }
  return (  
    <Row className="mt-4">
      {orders.map(parcel => (
          <div key={parcel.nft.activeOrder.txHash} className="mb-4 col-sm-12 col-md-12 col-lg-4">
            <Card>
              <CardHeader className="asset-header p-2 card-title text-center">Decentraland</CardHeader>
              <div>
                <div>
                  <CardTitle className="asset-title text-center">
                    <div className="current-price">Price:&nbsp;{Web3.utils.fromWei(`${parcel.nft.activeOrder.price}`,'ether')} MANA</div>
                  </CardTitle>   
                </div>  
              </div>  
              <Row className="justify-content-center">
                <NominationDetailsLink tokenId={parcel.nft.parcel.tokenId} drizzle={props.drizzle} />
              </Row>
              <CardBody className="pb-.4rem;px-2">
                <Row className="asset-image align-items-center">
                  <img className="mx-auto" src={'https://api.decentraland.org/v1/parcels/' + parcel.nft.parcel.x + '/' + parcel.nft.parcel.y + '/map.png?height=260&width=260&size=12'} />
                </Row>
                <Row className="mt-2 justify-content-center">
                <div className="my-2 asset-card text-center card-text">Parcel:&nbsp;{parcel.nft.parcel.x},{parcel.nft.parcel.y}</div>
                </Row>
                <Row className="mt-3 justify-content-center">
                  <div className="mb-2 col-sm asset-card text-left card-text">End Date</div>
                  <div className="mb-2 col-sm asset-card-time text-right card-text">
                    {(typeof(Number(parcel.nft.activeOrder.expiresAt)) == 'number')?
                      <Moment format="h:mma M/DD/YY">
                        {Number(parcel.nft.activeOrder.expiresAt)}
                      </Moment>       
                    :
                      'no end date'
                    }
                  </div> 
                </Row>  
              </CardBody>   
              <CardFooterButton 
                 tokenId={parcel.nft.parcel.tokenId} 
                 assetPrice={parcel.nft.activeOrder.price} 
                 expiresAt={parcel.nft.activeOrder.expiresAt} 
                 nftAddress={parcel.nft.activeOrder.nftAddress} 
                 orderId={parcel.nft.activeOrder.id} 
                 orderStatus={parcel.status}
                 props={props} 
                 storedOrderId={storedOrderId}
                 userManaBalance={userManaBalance}
                 getSoldStatus={getSoldStatus}
                 getOrderId={getOrderId}
              />
            </Card>  
          <style jsx> {` 
            .modal-content{border-top:2px solid #bff}
            .asset-card{font-weight:400;color:black;font-size:.9rem;font-family: Poppins;}
            .card-footer-btn{font-family: Poppins;font-weight:400;color:#000;font-size:1rem;
            letter-spacing:.3rem;border-radius:.01rem .01rem .2rem .2rem;}
            .card-footer-btn:hover{background:#007bff;color:white;}
            .asset-total-fund{font-weight:200;font-size:.9rem;letter-spacing:.2rem;color:#000;}
            .asset-card-numbers{font-size:1rem;letter-spacing:.2rem;color:#007bff;hover:black;}
            .asset-card-time{font-weight:400;color:#ff8080;font-size:.9rem;font-family:Poppins;}
            .current-price{font-size:.9rem;letter-spacing:.1rem;font-weight:400;}
            `}    
          </style>
        </div>   
      ))}
    </Row> 
  );
};

const mapStateToProps = (state) => ({
  storedOrderId: state.appReducers.storedOrderId 
})

const mapDispatchToProps = (dispatch) => ({
  getSoldStatus: (proposalBalance, soldStatus) =>  dispatch({ type: "GOT_SOLD_STATUS", status: soldStatus, proposalBalance: proposalBalance }),
  getOrderId: (storedOrderId) =>  dispatch({ type: "GOT_STORED_ORDER_ID", storedOrderId: storedOrderId })
});
  
  
export default connect(mapStateToProps, mapDispatchToProps)(Nominations);
