import React from 'react';
import { connect } from 'react-redux';
import { newContextComponents } from "@drizzle/react-components";
import { Button, Progress, Row, Form } from "shards-react";
import Web3 from "web3";
import ForSaleInnerModal from '../modals/ForSaleInnerModal.js';

//data.balance is 0 because it does not update until payout starts
const { ContractForm, ContractData } = newContextComponents;

const SoldAsset = ({drizzle, drizzleState, propAddress, tokenId, listPrice, startPay  }) => {
  const account = drizzleState.accounts[0];

  return(
    <>
      <ForSaleInnerModal
         drizzle={drizzle}
         drizzleState={drizzleState}
         propAddress={propAddress}
         listPrice={listPrice}
         startPay={startPay}
      />

      <ContractForm
        drizzle={drizzle}
        drizzleState={drizzleState}
        contract={propAddress}
        method="payOut" 
        sendArgs={{ from:account }}
        render={({ handleSubmit }) => (
          <Form onSubmit={handleSubmit}>
            <div className="justify-content-center mx-auto ">
              {/* <ContractData drizzle={drizzle} drizzleState={drizzleState} contract={propAddress} method="proposals" methodArgs={[tokenId]}  */}
              <ContractData drizzle={drizzle} drizzleState={drizzleState} contract={MANAtoken} method="balanceOf" methodArgs={propAddress}
                render={data => (
                  <>
                    <p className="mt-4 mb-1 text-center" >Current Pool Depth: {Web3.utils.fromWei(`${data}`,'ether')} MANA</p>
                    {/* <Progress value={Web3.utils.fromWei(`${balance}`,'ether')}  max={data.reSalePrice} ></Progress> */}
                  </>
                )}
              />
            </div> 
            <div className="modal-footer">
              <button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 16 }} > 
                Pay Out
              </button>
            </div>
            <style jsx> {` 
              .sell-text{font-weight:400;color:black;font-size:1rem;font-family: Poppins;}
              .amount-number {color:#66a3ff;font-family: Poppins} 
              `}    
            </style>
          </Form>
        )}
      />
    </>
  )
}

const mapStateToProps = (state) => ({
  listPrice:state.appReducers.listPrice
})

export default connect(mapStateToProps, null)(SoldAsset);
