import React from "react";
import Web3 from "web3";
import { toast } from 'react-toastify';
import { Button } from "shards-react";
//import { drizzleConnect, DrizzleContext } from '@drizzle/react-plugin';


const Input = ({ title, value, onChange }) => (
  <div>
     <label>
      {title}
    </label>
    <input className="form-control form-control-sm" value={value} onChange={onChange} /> 
  </div>
) 

class InitialContribForm extends React.Component {
  state = { 
    contribAmount:"",
    userId: "",
    text: "",
    listPrice: "",
    reSalePrice: ""
  };


// handleChange = e => {
//   this.setState({
//     contribAmount: e.target.value,
//   });
// }
updateField = (fieldName, e) => {
  const newState = {}
  newState[fieldName] = e.target.value

  this.setState(newState)
}

contribute = async (e) => {
  e.preventDefault() 

  // Some quick validation checks
  for (let key in this.state) {
    if (!this.state[key]) {
      return alert(`You must fill in your ${key}!`)
    }
  }

  const { contribution, text, reSalePrice, listPrice, userId } = this.state

  try {
    // Open the MetaMask modal:
    await contribute(contribution, text, reSalePrice, listPrice, userId)

    alert("Your user has been created!")
  } catch (err) {
    alert(`Sorry, we couldn't create your user: ${err}`)
  }
}

// contribute = async (e) => {
//   e.preventDefault() 

//   const { contribAmount } = this.state
 
  
//   const { drizzle } = this.props;

   
//     try {
//     contract.methods["contribute"].cacheSend(contribAmount);
//   } catch (err) {
//     console.error("Err:", err)
//   }


// }

  render() {

    return (
      <div>
        <form onSubmit={this.contribute}>
          <div className="contribute-form-dialog ">
          <Input 
            title="Initial Contribution" 
            onChange={e => this.updateField("contribution", e) } 
          />  
           <Input 
          title="Pitch" 
          onChange={e => this.updateField("text", e)} 
          />

          <Input 
            title="Sale Price" 
            onChange={e => this.updateField("reSalePrice", e)} 
          />

          <Input 
            title="Current Price" 
            onChange={e => this.updateField("listPrice", e)} 
          />

          <Input 
            title="Your UserId" 
            onChange={e => this.updateField("userId", e)} 
          />

          </div> 
          <div>
            <Button className="btn btn-block btn-success contribute-form-btn" key="submit" type="button" onClick={this.contribute} theme="success" onClick={this.props.handleClose}>
              <p className="mb-0">Contribute&nbsp;&nbsp;{this.props.state.valueSlider}&nbsp;ETH</p>
            </Button>

          </div>
        </form>
    
        <style jsx>
          {`  .contribute-form-btn {font-family: Poppins;letter-spacing:5.5px;}
              .contribute-form-dialog {font-family: Poppins; font-size:1rem;}
          `}                
        </style>
      </div>
    )
  }
  
}

export default InitialContribForm;
