import React, { Component, useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter, Button, Progress, Slider } from "shards-react";
import Web3 from "web3";
import CppContribSlider from '../sliders/CppContributeSlider';

const ContributeModal = ( {drizzleContext, assetData, state, handleSlide} ) => {
  
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  let value  = state.value;
  // this.setState({
  //   value: state.value,
    
  // })


  return ( 
    
        <>
          <CardFooter className="p-0">
            <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Fund Purchase</button>
          </CardFooter>
          
          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Fund Asset Purchase</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <div>
                  {assetData.sell_orders.map((subOne)=> 
                      {if(subOne.payment_token_contract.id === 1){
                        let maxRanger = Web3.utils.fromWei(subOne.current_price,'ether')
                        let maxRange = parseInt(maxRanger);
                        let minRange = 3;
                      return (
                        <div key={subOne.created_date.toString()}>
                        <div className="current-price">
                          <Progress value={7} max={Web3.utils.fromWei(subOne.current_price,'ether')} >{7}</Progress>
                          Current Price:{Web3.utils.fromWei(subOne.current_price,'ether')} ETH
                        </div>
                        <div>
                        <p className="mt-8 mb-1" >Current Pool Depth: {minRange} ETH</p>
                          <Slider className="mt-15" 
                            theme="success"
                            pips={{ mode: "steps", stepped: true, density: 3 }}
                            onSlide={handleSlide}
                            connect={[true, false]}
                            start={[value]}
                            range={{ min: minRange, max: maxRange }}
                          />
                        <p className="mt-8 mb-1">{value} ETH</p>

                      </div>                   
                      </div>
                      )}
                    }
                  )}
                </div>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
                </Button>
              <Button variant="primary" onClick={handleClose}>
                Fund
              </Button>
            </Modal.Footer>
          </Modal>
        </>
         );    
        } 

    export default ContributeModal;
