import React from "react";
import { Button } from "shards-react";
import RegModal from '../RegModal.js';
 
 export default class JoinButton extends React.Component {
  constructor(props) {
    super(props);

    this.toggleDropdown = this.toggleDropdown.bind(this);
    this.toggleNavbar = this.toggleNavbar.bind(this);

    this.state = {
      dropdownOpen: false,
      collapseOpen: false,
      showRegisterModal: false,
      ethNetwork: null,
      networkId: null
    };
  }

  
  toggleRegisterModal = async () => {
    const { showRegisterModal } = this.state

    this.setState({
      showRegisterModal: !showRegisterModal,
    })
  }

  toggleDropdown() {
    this.setState({
      ...this.state,
      ...{
        dropdownOpen: !this.state.dropdownOpen
      }
    });
  }

  toggleNavbar() {
    this.setState({
      ...this.state,
      ...{
        collapseOpen: !this.state.collapseOpen
      }
    });
  }

  render() {
    const { showRegisterModal } = this.state;
      return ( 
            <div>
              <Button className='nav-join-btn' onClick={this.toggleRegisterModal} onClose={this.toggleRegisterModal} theme="success" size="md" className="my-2 mr-1">
                JOIN
              </Button>

              <div>
                {showRegisterModal && (
                  <RegModal onClose={this.toggleRegisterModal} />
                )}
              </div> 

              <style jsx>
                {` .nav-join-btn {font-family: Poppins;letter-spacing: 2.5px;}`}  
              </style>
            </div>  
        )  

        
    }
}
