import React from 'react';
import App from './innerTestPage';
import { composeWithDevTools } from 'redux-devtools-extension';
import createSagaMiddleware from 'redux-saga';
import { createStore, applyMiddleware, compose } from 'redux';
import { DrizzleContext, DrizzleProvider } from "@drizzle/react-plugin";  
import { generateStore, generateContractsInitialState } from "@drizzle/store";
import options  from "../EthComponents/drizzleOptions";

//CREATE STORE
import root from '../middleware/sagas/root';
import reducer from '../middleware/reducers/reducer.js'

//GENERATE STORE
import rootSaga from '../middleware/sagas/appSagas';
import appReducer from '../middleware/reducers/appReducers.js'



//APPMIDDLEWARES
import { contractEventSaga, joinTransactionSaga } from '../middleware/capMiddleWare';
const contractEventNotifier = contractEventSaga;
const joinTransactionComplete = joinTransactionSaga;
const appMiddlewares = [ contractEventNotifier(), joinTransactionComplete ];

//GENERATE STORE
const appReducers = {appReducers: appReducer};
const appSagas = [root];


//CREATE STORE
const initialState = {
  contracts: generateContractsInitialState(options)
};

const sagaMiddleware = createSagaMiddleware();

const allMiddlewares = [...appMiddlewares, sagaMiddleware];

const store = createStore(
      reducer,
      initialState,
    composeWithDevTools(
      applyMiddleware(
        ...allMiddlewares
      )
    )
);

// const store = generateStore({
//   drizzleOptions,
//   appReducers,
//   appSagas,
//   appMiddlewares,
//   disableReduxDevTools: false  
// })



sagaMiddleware.run(appSagas);

class TestPage extends React.Component {


  render() {
    return (
     
      <div>
           {/* <DrizzleContext.Provider> */}
        <DrizzleProvider options={options} store={store} >
 
          <App />
       
        </DrizzleProvider>
        {/* </DrizzleContext.Provider> */}
      </div>
    );
  }
}



export default TestPage;
