import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import NominationForm from '../forms/NominationForm';
import { connect } from 'react-redux';
import Web3 from 'web3';
import ProposalForm from '../forms/ProposalForm';
import ProposalAddress from '../ProposalAddress';
   

const NominationModal = ({ parcel, props, userManaBalance, getTokenId, propAddress, tokenIdState  }) => {

  const {drizzleState, drizzle} = props;

  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);
  //const [tokenId, setTokenId] = useState(null);
  //const [loading, setLoading] = useState(true);
  //const [dataKey, setDataKey] = useState(null);
  ///const[drizzle, setDrizzle] = useState(props.drizzle)
  //const [currentProp, setCurrentProp] = useState(null);

  const myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");

  useEffect(() => {
  const fetchDetailsLink = async () => {

  const graphql= JSON.stringify({ query: 
  `{ parcels(where: {x:"${parcel.x}",y:"${parcel.y}"}) { tokenId }}`
})

  const proxyUrl = 'https://cors-anywhere.herokuapp.com/',
  targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'

  fetch(proxyUrl + targetUrl, {
    method: "POST",
    headers:myHeaders,  
    body:graphql, variables:{},
    redirect: 'follow'
  })
    .then(res => res.json())
    .then((result) => {
      const parcelData = result.data.parcels[0];
      return parcelData;
    })
    .then((result) => { 
      const { tokenId } = result;
      getTokenId(tokenId)
      return tokenId;
      //drizzle.store.dispatch({ type: "SET_TOKEN_ID", tokenId:tokenId });
    })
    .then((tokenId) => { 
    //   { tokenId !== 'undefined' ? 
      
    //     "Loading..."
    //     : 
    //     <ProposalAddress drizzle={drizzle} drizzleState={drizzleState} tokenIdState={tokenIdState} />
    //       const dataKey = drizzle.contracts.ProposalController.methods["getPropTokenIdMap"].cacheCall(`${tokenId}`);      
    //       const { ProposalController } = drizzleState.contracts;
    //       const displayData = ProposalController.getPropTokenIdMap[`${dataKey}`];
    //       const proposalAddress =  displayData && displayData.value;
    //       console.log(proposalAddress)
    //       //drizzle.store.dispatch({ type: "GOT_PROP_ADDRESS", propAddress:proposalAddress });
    // }
        //}
    })
  };
fetchDetailsLink();

}, []);


return (   
      <> 
      { tokenIdState ? 
      <ProposalAddress drizzle={drizzle} drizzleState={drizzleState} tokenIdState={tokenIdState} />
      : null }
      <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Nominate Asset</button>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header className="text-center justify-content-center" closeButton>
            <Modal.Title className="text-center justify-content-center">Asset Price:&nbsp;{parcel.publication.price}&nbsp;MANA</Modal.Title>
          </Modal.Header>
          <Modal.Body className="pt-1">
          <ProposalForm                  
                  assetPrice={parcel.publication.price}  
                  props={props}
                  drizzle={drizzle}
                  drizzleState={drizzleState}
                  tokenId={tokenIdState}
                  userManaBalance={userManaBalance}
                  //propAddress={propAddress}
            /> 
              {/* <NominationForm                   
                drizzle={props.drizzle}
                drizzleState={props.drizzleState}  
                assetPrice={parcel.publication.price}  
                props={props}
                tokenId={tokenId}
                userManaBalance={userManaBalance}
                />  */}
          </Modal.Body>
        </Modal>
      </>
        );    
      } 
        const mapStateToProps = (state) => ({
          tokenIdState: state.appReducers.tokenId,
          propAddress: state.appReducers.propAddress,
        })
        
        const mapDispatchToProps = (dispatch) => ({
          getTokenId: (tokenId) =>  dispatch({ type: "SET_TOKEN_ID", tokenId:tokenId }),
          //onSetDrizzleState: (drizzle) => dispatch({ type: "SET_DRIZZLE_STATE", drizzle: drizzle }),
          //getCurrentProp: (drizzle,dataKey) =>  dispatch({ type: "GET_PROP_ADDRESS",drizzle:drizzle,dataKey:dataKey })
        });
        
        
export default connect(mapStateToProps, mapDispatchToProps)(NominationModal);

    //export default NominationModal;
