import { generateStore } from '@drizzle/store'
import drizzleOptions from '../drizzleOptions'
import { toast } from 'react-toastify'
import { contractEventSaga, joinTransactionSaga } from './capSagas';
import reducer from './capReducers';
//import capReducer from './capReducers';
import rootSaga from './capSagas';
// const appReducers = { todos: todosReducer }
const capSagas = [rootSaga];
//const pReducer = {capReducer};
const contractEventNotifier = contractEventSaga;

const joinTransactionNotifier = joinTransactionSaga;

const appMiddlewares = [ contractEventNotifier, joinTransactionNotifier ]

const store = generateStore({
  drizzleOptions,
  appMiddlewares,
  reducer,
  //pReducer,
  capSagas,
  disableReduxDevTools: false 
})

//appMiddlewares.run(rootSaga);

export default store;
