    // onSetDrizzleState(context.drizzle);
  }

  render() {
    const { classes, onCheckingTxDone } = this.props;
    return (
      <Router>
        <div className="App">

        </div>
      </Router>
    );
  }
}

App.propTypes = { 
  classes: PropTypes.object.isRequired,
};

App.contextTypes = {
  drizzle: PropTypes.object
}

const mapStateToProps = state => {
  return {
    propAddress: state.appReducer.propAddress,
    drizzleStatus: state.drizzleStatus,
    UserStorage: state.accounts[0]
  }
}

const mapDispatchToProps = dispatch => {
  return {
    getCurrentProp: () =>  dispatch({ type: "GET_PROP_ADDRESS",propAddress:2 })
    //onSetDrizzleState: (drizzle) => dispatch({ type: "SET_DRIZZLE_STATE", drizzle: drizzle })
  };
};

export default drizzleConnect(withStyles(styles)(App), mapStateToProps, mapDispatchToProps);
