import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Web3 from 'web3';
import { Loader } from 'rimble-ui';
import { CardTitle, Row } from "shards-react";

const ForSaleModal = ({ drizzle, propAddress, assetPrice, listPrice }) => {
  
  const [deposits, setDespositInfo] = useState([]);
  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  useEffect(() => {
    fetchProposalInfo();
  }, [])

  const fetchProposalInfo = async () => { 
      let state = drizzle.store.getState()
      if(state.drizzleStatus.initialized){
      const proposalContract = await drizzle.contracts[`${propAddress}`];
      const depositInfoLength = await proposalContract.methods.countDepositers().call();

      let i;
      let depositerArray = [];
      for (i = 0; i < depositInfoLength; i++) {
      const depositInfo = await proposalContract.methods.depositers([i]).call();
      depositerArray.push([depositInfo.depositer, depositInfo.amount]);
      setDespositInfo(depositerArray);
    }
   
  }
   return 'Loading....'
  }

return (   
      <>   
            {deposits ?   
           
                <Row className="card-text justify-content-center mt-1"> 
                  <div className="justify-content-left">
                    <Row>
                      <h6>
                        Purchase Price:<span className="purchase-price">{Web3.utils.fromWei(listPrice,'ether')}</span> MANA
                      </h6>
                    </Row>
                    <Row>
                      <h5 className="deposit-text">
                        NFT INVESTORS:
                      </h5>
                    </Row>
                  {deposits.map(deposit => (
                    <div className="deposit-info" key={deposit[0]} >
                      <Row> 
                        <div>
                          <p className="deposit-text mb-1">
                            {deposit[0]}
                          </p>
                          <p className="mb-1">
                            Contribution: <span className="purchase-price">{Web3.utils.fromWei(deposit[1],'ether')}</span> MANA
                          </p>
                        </div>
                      </Row>
                    </div>
                  ))}
                  </div>
                </Row>
              :
                <div className="align-items-center">   
                  <Loader className="mx-auto" color="#007bff" size="40px" />
                </div>
              } 
            <style jsx>
              {` 
                .card-text{font-family:Poppins;font-size:1rem;}
                .purchase-price{color:#007bff;font-size:.9rem;}
                .deposit-info{border-bottom: 1px solid #dfe1e3;}
                .current-price-title{font-size:1.3rem;}
                .deposit-text{font-size:1rem;color:#000;}
              `}                
            </style>
        </>
        );    
      } 

const mapStateToProps = (state) => ({
  listPrice:state.appReducers.listPrice
})

export default connect(mapStateToProps, null)(ForSaleModal);
      



