import React from 'react';
import { connect } from 'react-redux';
import Accordion from 'react-bootstrap/Accordion';
import { Button, Row, Form } from "shards-react";
import Web3 from "web3";

import UpdatePrice from '../buttons/UpdatePropBtn.js';
import UpdatePropBtn from '../buttons/UpdatePriceBtn.js';
import UpdateExpire from '../buttons/UpdateExpireBtn.js';


const UpdateForm =  ({ drizzle, drizzleState, assetPrice, listPrice, propAddress, orderId, prevOrderStatus, expiresAt, currentExpiresAt, storedOrderId  }) => { 


 const update = async () => {
  const account = await drizzleState.accounts[0];
  const contract = await drizzle.contracts[`${propAddress}`];
  //uint256 _newPrice, uint256 _expiresAt, bytes16 _orderId
  try {
      contract.methods["updateContract"].cacheSend(assetPrice, currentExpiresAt, expiresAt, {from:`${account}`});
    } catch (err) {
      console.error("Err:", err)
    }
  }

  
if(!prevOrderStatus && !listPrice && !expiresAt) {  
  return <></>;
}

// if(prevOrderStatus && listPrice && expiresAt)  {
//   return <>k</>;
// }
else if(prevOrderStatus === 'cancelled') {
  //else if(!prevOrderStatus) {
    return (
      <Accordion defaultActiveKey="1">
        <Row className="mt-2 mb-1 justify-content-center">{console.log(storedOrderId)}
          <div className="justify-content-left">
            <Accordion.Toggle as={Button} className="px-4 py-1" variant="link" eventKey="0" theme="warning" outline >
            {/* <div className="amount-text" >Update Price:</div> */}
              <Row className="mt-1 justify-content-center">
                <h5 className="text-dark display-5 mb-1">Update:</h5>
              </Row>
              {/* {(() => {  
                if(prevOrderStatus === 'cancelled') { */}
                  <Row className="mt-1 mb-1">
                    <div className="order-id-text d-inline-block">New Id:<span className="text-danger order-id-text d-inline-block text-truncate">0x442e276a9572ae5df715790f95e5433f4d4e258981d3e0d42286760760b1bfe4 {orderId}</span></div>
                  </Row>
                  <Row className="mt-1 mb-1">
                    <div className="order-id-text d-inline-block">Old Id:<span className="text-danger order-id-text d-inline-block text-truncate">{storedOrderId}</span></div>
                  </Row>
                {/* }
              })} */}
              {(() => {
                if(listPrice != assetPrice && listPrice != 0) {
                  <>
                    <Row className="mt-1">
                      <div className="text-dark text-primary amount-text" >Current Price: <span className="amount-number">{Web3.utils.fromWei(`${assetPrice}`,'ether')}</span> MANA</div>
                    </Row>
                    <Row className="mt-1">
                      <div className="text-dark text-danger amount-text" >Original Price: <span className="original-amount-number">{Web3.utils.fromWei(`${listPrice}`,'ether')}</span> MANA</div>
                    </Row>
                  </>
                }
              })}
              {(() => {
                if(expiresAt != 0 && expiresAt != currentExpiresAt)  {  
                  <>   
                    <Row className="mt-1">
                      <div className="text-dark text-danger mr-1 text-right card-text">Old Date:</div>
                      <div className=" expire-text text-left card-text">
                        {(typeof(Number(expiresAt)) == 'number')?
                          <Moment format="h:mma M/DD/YY">
                            {Number(expiresAt)}
                          </Moment>       
                        :
                          'no end date'
                        }
                      </div> 
                    </Row>  
                    <Row className="mt-1">
                      <div className="text-dark text-primary mr-1 text-right card-text">New Date:</div>
                      <div className="expire-text text-left card-text">
                        {(typeof(Number(currentExpiresAt)) == 'number')?
                          <Moment format="h:mma M/DD/YY">
                            {Number(currentExpiresAt)}
                          </Moment>       
                        :
                          'no end date'
                        }
                      </div> 
                    </Row>  
                  </>
                }
              })}
            </Accordion.Toggle>
              <Accordion.Collapse eventKey="0">
                <Row className="justify-content-center">
                  <Form onSubmit={update}>
                    <Button outline theme="success" size="sm" className="mt-2 mx-auto"
                      key="submit"
                      type="button"
                      onClick={update} 
                      style={{ fontSize: 12 }}
                    >
                      Update Contract
                    </Button>
                  </Form>
                </Row>
              </Accordion.Collapse>
          </div> 
        </Row>
        <style jsx> {` 
            .order-id-text{max-width: 150px;color:#000;font-family: Poppins;}
            .amount-text {color:#000;font-family: Poppins;}
            .amount-number {color:#66a3ff;font-family: Poppins}
            .original-amount-number{color:#c4183c;font-family: Poppins}
            .expire-text {color:#000;font-family: Poppins;}
          `}    
        </style>
      </Accordion>
    )
  }
  else{
    return <>ddddd</>;
  }


}
  

const mapStateToProps = (state) => ({
  listPrice:state.appReducers.listPrice,
  assetPrice:state.appReducers.assetPrice,
  expiresAt:state.appReducers.expiresAt,
  currentExpiresAt:state.appReducers.currentExpiresAt,
  storedOrderId:state.appReducers.storedOrderId
})



export default connect(mapStateToProps, null)(UpdateForm);
  
  
  //export default UpdateForm;
