import React from 'react';
import Web3 from "web3";
import { Loader } from 'rimble-ui';
import { Card, CardHeader, CardBody, CardFooter, CardTitle, Button, Row } from "shards-react";
import NominationModal from './modals/NominationModal';
import NominationDetailsLink from './NominationDetailsLink';
import Moment from "react-moment";

const Nominations = ({ orders, loading, props, userManaBalance }) => {

  if (loading) {
    return <Loader className="mt-2 mx-auto" size="30px" color="blue" />
  }
  return (  
    <Row className="mt-4">
      {orders.map(parcel => (
          <div key={parcel.activeOrder.txHash} className="mb-4 col-sm-12 col-md-12 col-lg-4">
            <Card>
              <CardHeader className="asset-header p-2 card-title text-center">Decentraland</CardHeader>
              <div>
                <div>
                  <CardTitle className="asset-title text-center">
                    <div className="current-price">Price:&nbsp;{Web3.utils.fromWei(`${parcel.activeOrder.price}`,'ether')} MANA</div>
                  </CardTitle>   
                </div>  
              </div>  
              <Row className="justify-content-center">
                <NominationDetailsLink tokenId={parcel.parcel.tokenId} drizzle={props.drizzle} />
              </Row>
              <CardBody className="pb-.4rem;px-2">
                <Row className="asset-image align-items-center">
                  <img className="mx-auto" src={'https://api.decentraland.org/v1/parcels/' + parcel.parcel.x + '/' + parcel.parcel.y + '/map.png?height=260&width=260&size=12'} />
                </Row>
                <Row className="mt-2 justify-content-center">
                <div className="my-2 asset-card text-center card-text">Parcel:&nbsp;{parcel.parcel.x},{parcel.parcel.y}</div>
                </Row>
                <Row className="mt-3 justify-content-center">
                  <div className="mb-2 col-sm asset-card text-left card-text">End Date:
                  <div className="mb-2 asset-card-time card-text">
                    {(typeof(Number(parcel.activeOrder.expiresAt)) == 'number')?
                      <Moment format="h:mma M/DD/YY">
                        {Number(parcel.activeOrder.expiresAt)}
                      </Moment>       
                    :
                      'No End Date'
                    }
                  </div> 
                  </div>
                </Row>  
              </CardBody>     
              <CardFooter className="p-0">
                <NominationModal 
                  parcel={parcel} 
                  props={props} 
                  userManaBalance={userManaBalance}
                />
              </CardFooter>
            </Card>  
          <style jsx> {` 
            .asset-card{font-weight:400;color:black;font-size:.9rem;font-family: Poppins;}
            .card-footer-btn{font-family: Poppins;font-weight:400;color:#000;font-size:1rem;
            letter-spacing:.3rem;border-radius:.01rem .01rem .2rem .2rem;}
            .card-footer-btn:hover{background:#007bff;color:white;}
            .asset-total-fund{font-weight:200;font-size:.9rem;letter-spacing:.2rem;color:#000;}
            .asset-card-numbers{font-size:1rem;letter-spacing:.2rem;color:#007bff;hover:black;}
            .asset-card-time{font-weight:400;color:#ff8080;font-size:.9rem;font-family:Poppins;}
            .current-price{font-size:.9rem;letter-spacing:.1rem;font-weight:400;}
            `}    
          </style>
        </div>   
      ))}
    </Row> 
  );
};

export default Nominations;
