import React from 'react';

class EthAccounts extends React.Component {
 state = { dataKey: null };

 componentDidMount() {
   const { drizzle } = this.props;
   const contract = drizzle.contracts.UserStorage;
   let dataKey = contract.methods["usernames"].cacheCall(); // declare this call to be cached and synchronized
   this.setState({ dataKey });
 }

 render() {
   const { UserStorage } = this.props.drizzleState.contracts;
   const displayData = UserStorage.usernames[this.state.dataKey]; // if displayData (an object) exists, then we can display the value below
   return (
     <p>Hi from Truffle! Here is your storedData: {displayData && displayData.value}</p>
   )
 }
}

export default EthAccounts

