import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import { Button } from "shards-react";
import { newContextComponents } from "@drizzle/react-components";
import Web3 from "web3";
const { ContractForm } = newContextComponents;

const WithDraw = ({ depositAmount, drizzle, drizzleState, propAddress }) => { 
if(depositAmount > 0) {  
return (
    <Accordion defaultActiveKey="1">
      <div>
        <Accordion.Toggle as={Button} className="px-2.5 py-1" variant="link" eventKey="0" theme="light">
        <div className="amount-text mt-1 mb-1" >Your Deposit: <span className="amount-number">{Web3.utils.fromWei(`${depositAmount}`,'ether')}</span> MANA</div>
        </Accordion.Toggle>
          <Accordion.Collapse eventKey="0">
              <div>
                <ContractForm
                  drizzle={drizzle}
                  drizzleState={drizzleState}
                  contract={propAddress}
                  method="withdraw"
                  sendArgs={{ from:drizzleState.accounts[0]}}
                  render={({ handleSubmit }) => (
                    <Button outline theme="danger" size="sm" className="mt-2"
                      key="submit"
                      type="button"
                      style={{ fontSize: 15 }}
                      onClick={handleSubmit}
                    >
                     WithDraw {Web3.utils.fromWei(`${depositAmount}`,'ether')} MANA
                    </Button>
                  )}
                /> 
              </div>
          </Accordion.Collapse>
      </div> 
      <style jsx> {` 
          .details-btn:hover{font-family: Poppins;font-size:.9rem;}
          .amount-text {font-size:.9rem;color:#000;font-family: Poppins;}
          .amount-number {font-size:.9rem;color:#66a3ff;font-family: Poppins}
        `}    
      </style>
    </Accordion>
  )
}
  else  {
    return null;
  }

}

export default WithDraw;
