import React from 'react';
import Navbar from './Navbar.js';
import HeadOne from './Head.js';
import Footer from './Footer.js'
import { Provider } from 'react-redux';
import { DrizzleContext } from "@drizzle/react-plugin"; 
import { ToastContainer } from 'react-toastify';   
import { Drizzle, generateStore, generateContractsInitialState, drizzleSagas } from "@drizzle/store";
import drizzleOptions  from "../../EthComponents/drizzleOptions";
import { contractEventSaga, joinTransactionSaga } from '../../middleware/capMiddleWare';
import  reducer from '../../middleware/reducers/appReducers.js'
import createSagaMiddleware from "@drizzle/store/node_modules/redux-saga";
import { createStore, applyMiddleware, compose, combineReducers } from 'redux'
import "regenerator-runtime/runtime";
//Old Store
import rootSaga from '../../middleware/rootSaga';

const contractEventNotifier = contractEventSaga;
const joinTransactionComplete = joinTransactionSaga;
const appMiddlewares = [contractEventNotifier(), joinTransactionComplete ];
const appReducers = {appReducers: reducer};

const sagaMiddleware = createSagaMiddleware()
  const allMiddlewares = [...appMiddlewares, sagaMiddleware]
  //const allReducers = { ...drizzleReducers, ...appReducers }
  let initialContractsState = {
    contracts: generateContractsInitialState(drizzleOptions)
  }
  const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;




const store = createStore(
  appReducers,
    initialContractsState,
    // appMiddlewares,
    composeEnhancers(applyMiddleware(...allMiddlewares))
);

sagaMiddleware.run(composeSagas([...drizzleSagas, ...rootSaga]))

// const store = generateStore({
//     drizzleOptions,
//     appReducers,
//     appSagas,
//     appMiddlewares,
//     disableReduxDevTools:false
// })



const drizzle = new Drizzle(drizzleOptions, store);

export const Page = ({ children }) => (
  <div>
    <DrizzleContext.Provider drizzle={drizzle} >
      <Provider store={store}>{console.log(store.getState())}
      <HeadOne />
      <Navbar />  
        <main>
          <ToastContainer />
          {children}
        </main>
      <Footer />
      </Provider>  
    </DrizzleContext.Provider>
  </div>
) 

export const Center = ({ children }) => (
    <div>
      {children}   
    </div>
  )
