import React from 'react';
import Web3 from "web3";
import { Loader } from 'rimble-ui';
import Accordion from 'react-bootstrap/Accordion';
import { Card, CardHeader, CardBody, CardFooter, CardTitle, Button, Row, Progress } from "shards-react";
import NominationModal from './modals/NominationModal';
import moment from 'moment';

const Nominations = ({ parcels, loading }) => {
  if (loading) {
    return <Loader size="30px" color="blue" />
  }
  return (
    <Row className="mt-4">
      {parcels.map(parcel => (
          <div key={parcel.publication.tx_hash} className="mb-4 col-sm-12 col-md-12 col-lg-4">
            <Card>
              <CardHeader className="asset-header p-2 card-title text-center">Decentraland</CardHeader>
              <div>
                <div>
                  <CardTitle className="asset-title text-center">
                    <div className="current-price">Price:&nbsp;{parcel.publication.price} MANA</div>
                  </CardTitle>
                </div>
              </div>
              <Row className="justify-content-center">
                <Accordion defaultActiveKey="1">
                  <div>  
                    <Accordion.Toggle as={Button} variant="link" eventKey="0" outline theme="light">
                      <div>Property Details</div>
                    </Accordion.Toggle>
                      <Accordion.Collapse eventKey="0">
                        <div className="p-3 mt-3 border rounded">
                          <div>
                            <a  target="_blank">Decentraland</a>
                          </div>
                          <div>
                            <a href={`https://market.decentraland.org/contracts/0xf87e31492faf9a91b02ee0deaad50d51d56d5d4d/tokens/115792089237316195423570985008687907844762925492617102452873218822118924353538`}  target="_blank">OpenSea</a>
                          </div>
                        </div>
                      </Accordion.Collapse>
                  </div> 
                </Accordion>
              </Row>
              <Row className="justify-content-center">
              
              </Row>
              <CardBody className="pb-.4rem;px-2">
                <a href='https://market.decentraland.org/contracts/0xf87e31492faf9a91b02ee0deaad50d51d56d5d4d/tokens/115792089237316195423570985008687907844762925492617102452873218822118924353538' target="_blank">
                  <Row className="asset-image align-items-center">
                    <img className="mx-auto" src={'https://api.decentraland.org/v1/parcels/' + parcel.x + '/' + parcel.y + '/map.png?height=260&width=260&size=12'} />
                  </Row>
                </a>
                <Row className="mt-2 justify-content-center">
                {(typeof(parcel.data.name) == 'string') ?
                    <div className="mb-2 asset-card text-center card-text">{parcel.data.name}</div>            
                    :
                    <div className="my-2 asset-card text-center card-text">Parcel:{parcel.id}</div>
                    }
                </Row>
                <Row className="mt-3 justify-content-center">
                  <div className="mb-2 col-sm asset-card text-left card-text">End Date:</div>
                  <div className="mb-2 col-sm asset-card text-right card-text">
                      {(typeof(parcel.publication.expires_at)== 'number')?
                      <div>{moment(parcel.publication.expires_at).day}</div>            
                    :
                    'no end date'
                    }
                  </div> 
                </Row>
              </CardBody>     
                {/* <CardFooter className="p-0">
                <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={this.toggleModal}>Nominate Asset</button>
                  <NominationModal 
                    toggleModal={this.toggleModal}
                    state={this.state} 
                    parcel={parcel} 
                    drizzle={this.props.drizzle}
                    drizzleState={this.props.drizzleState} 
                  />
                </CardFooter> */}
            </Card>  
           

          <style jsx> {` 
            .asset-card{font-weight:400;color:black;font-size:.9rem;font-family: Poppins;}
            .card-footer-btn{font-family: Poppins;font-weight:400;color:#000;font-size:1rem;
            letter-spacing:.3rem;border-radius:.01rem .01rem .2rem .2rem;}
            .card-footer-btn:hover{background:#007bff;color:white;}
            .asset-total-fund{font-weight:200;font-size:.9rem;letter-spacing:.2rem;color:#000;}
            .asset-card-numbers{font-size:1rem;letter-spacing:.2rem;color:#007bff;hover:black;}
            .current-price{font-size:.9rem;letter-spacing:.1rem;font-weight:300;}
            `}    
          </style>
        </div>   
      ))}
    </Row> 
  );
};

export default Nominations;
