import React, { Component } from 'react';
import { connect } from 'react-redux';


   
class InterMediateForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dataKey:null
    }
  }

 async componentDidMount() {   
    const { drizzle, drizzleState, propAddress, tokenId } = this.props;
    const propContract = await drizzle.contracts[`${propAddress}`];
    const dataKey = propContract.methods['proposals'].cacheCall(tokenId);
    this.setState({
      dataKey
    });
  }
  
  render() {
    return (
     <div>
       {this.state.dataKey}
     </div>
    )
  }
}   


const mapStateToProps = (state) => ({
  proposalStorage: state.appReducers.proposalStorage
})

const mapDispatchToProps = (dispatch) => ({
  getPropAbi: (ProposalStorageAbi) =>  dispatch({ type: "GOT_PROP_ABI", proposalStorageAbi:ProposalStorageAbi })
});

export default connect(mapStateToProps, mapDispatchToProps)(InterMediateForm);
