import React, { Component, useEffect, useState } from 'react';
import ContributeInnerModal from './ContributeInnerModal.js';
import ProposalStorageAbi from '../../artifacts/contracts/ProposalStorageAbi.json';
import { connect } from 'react-redux';
   
class InitialContributeModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newAbi:null
    }
  }

  componentDidMount() {   
    const newAbi = ProposalStorageAbi;
    this.setState({
      newAbi: newAbi
    });
  }
  
  render() {
   
    return (
    
    <ContributeInnerModal
      propAddress={this.props.propAddress} 
      userManaBalance={this.props.userManaBalance}
      assetPrice={this.props.assetPrice} 
      drizzleState={this.props.drizzleState} 
      drizzle={this.props.drizzle} 
      tokenId={this.props.tokenId} 
      newAbi= {this.props.newAbi}
    />
    )
  }
  

}

const mapStateToProps = (state) => ({
  newAbi: state.newAbi
})

const mapDispatchToProps = (dispatch) => ({
  getCurrentProp: (proposalAddress) =>  dispatch({ type: "GOT_PROP_ADDRESS", proposalAddress:proposalAddress })
});
      
      
export default connect(mapStateToProps, mapDispatchToProps)(InitialContributeModal);
