import React from 'react';
import { ContractForm, AccountData, ContractData } from "drizzle-react-components";


const EthComponent = () => (
<div>
  <h2>Balance of first account</h2>
  <AccountData accountIndex={0} units={"ether"} precision={3} />

  <h2>getData</h2>
  <ContractData contract="Storage" method="getData" to />

  <h2>setData</h2>
  <ContractForm 
    contract="Storage" 
    method="setData" 
    labels={['new value of `data` '
    ]} />

</div>
);

export default EthComponent;

