//import ReactPaginate from "react-paginate";
import Pagination from '../Pagination.js';
import * as Web3 from 'web3';
import { OpenSeaPort, Network, OpenSeaAsset  } from 'opensea-js';
import React, { Component, useEffect, useState } from 'react';
import OpenSeaApi from "./OpenSeaApi.js";
import { OrderSide } from 'opensea-js/lib/types';
import { Button, Form } from "shards-react";

function TestOsSdk() {
  const [items, setItems] = useState([]);

  const [pageCount, setpageCount] = useState(0);

  const provider = new Web3.providers.HttpProvider('https://mainnet.infura.io');

const seaport = new OpenSeaPort(provider, {
  networkName: Network.Main,
  apiKey: '2f2a0900458f4bdfbb489680a58e7526'
});
const options = {
  method: 'GET',
  headers: {Accept: 'application/json', 'X-API-KEY': '2f2a0900458f4bdfbb489680a58e7526'}
};

  let limit = 20;
  

  useEffect(() => {
 
    const getComments = async () => {
  
      const res = await fetch(
        //`http://localhost:3004/comments?_page=1&_limit=${limit}`
         //`https://jsonplaceholder.typicode.com/comments?_page=1&_limit=${limit}`
         `https://api.opensea.io/wyvern/v1/orders?bundled=false&include_bundled=false&side=1&limit=${limit}&offset=0&order_by=created_date&order_direction=desc`,options
      );
      const data = await res.json();
      console.log(res)
      // const total = res.headers.get("x-total-count");
      const total = res.count;
      setpageCount(Math.ceil(total / limit));
      // console.log(Math.ceil(total/12));
      setItems(data);
    };

    getComments();
  }, [limit]);

  const fetchComments = async (currentOs) => {
    const res = await fetch(
      //`http://localhost:3004/comments?_page=${currentPage}&_limit=${limit}`
       //`https://jsonplaceholder.typicode.com/comments?_page=${currentPage}&_limit=${limit}`
       `https://api.opensea.io/wyvern/v1/orders?bundled=false&include_bundled=false&side=1&limit=${limit}&offset=${currentOs}&order_by=created_date&order_direction=desc`,options
    );
    const data = await res.json();
    return data;
  };

  const handlePageClick = async (data) => {
    console.log(data.selected);

    let currentOs = data.selected + 20;

    const commentsFormServer = await fetchComments(currentOs);

    setItems(commentsFormServer);
    // scroll to the top
    //window.scrollTo(0, 0)
  };
  return (
    <div className="container">
      <div className="row m-2">
      <div className="modal-footer">
        <button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handlePageClick} style={{ fontSize: 16 }} > 
          ACCESS CAP
        </button>
      </div>      
        {/* {items.map((item) => {
          return (
            <div key={item.id} className="col-sm-6 col-md-4 v my-2">
              <div className="card shadow-sm w-100" style={{ minHeight: 225 }}>
                <div className="card-body">
                  <h5 className="card-title text-center h2">Id :{item.id} </h5>
                  <h6 className="card-subtitle mb-2 text-muted text-center">
                    {item.email}
                  </h6>
                  <p className="card-text">{item.body}</p>
                </div>
              </div>
            </div>
          );
        })} */}
      </div>

      <Pagination
        previousLabel={"previous"}
        nextLabel={"next"}
        breakLabel={"..."}
        pageCount={pageCount}
        marginPagesDisplayed={2}
        pageRangeDisplayed={3}
        onPageChange={handlePageClick}
        containerClassName={"pagination justify-content-center"}
        pageClassName={"page-item"}
        pageLinkClassName={"page-link"}
        previousClassName={"page-item"}
        previousLinkClassName={"page-link"}
        nextClassName={"page-item"}
        nextLinkClassName={"page-link"}
        breakClassName={"page-item"}
        breakLinkClassName={"page-link"}
        activeClassName={"active"}
      />
    </div>
  );
}

export default TestOsSdk;
