    console.log(cursor)
    const fetchPosts = async () => { 

    var proxyUrl = `https://cors-anywhere.herokuapp.com/`,
    targetUrl = `https://api.opensea.io/api/v1/assets?`;
    let contractAddress = `order_direction=desc&asset_contract_addresses=0x8a90CAb2b38dba80c64b7734e58Ee1dB38B8992e&limit=20&${cursor}&include_orders=true`
    let i;
    const callsPerProject = 5;
    //let offset = 0;
    for (i = 0; i < callsPerProject; i++) {
      
 
      fetch(targetUrl + contractAddress,{Accept: 'application/json', 'X-API-KEY': '2f2a0900458f4bdfbb489680a58e7526'})
      //fetch(targetUrl + contractAddress,{header:'x-api-key:2f2a0900458f4bdfbb489680a58e7526'})
        .then(res => res.json())
        .then((result) => {
            orderArray.push(result.assets);
            cursor=(result.next);
            
            setOrders(...orderArray);
            //setOffset(+ 50);
          },
          (error) => {
            setLoading(true),
            error;
          }
        )   
        .then(() => {
          setOrders(orderArray);
          //console.log(orderArray)
          setLoading(false);
        },
        (error) => {
          return error;
          }
        )          
  } 
}
   fetchPosts();
 }, []);

  

  return (
    <>
      <div>Hello</div>
      {/* <InnerAbCard props={props}   /> */}
    </>


  
  );
};

export default NewTest;
